const nodemailer = require('nodemailer');

// Функция для отправки почтового сообщения
function send(senderEmail, senderPassword, message) {
  // Создаем SMTP транспорт для отправки почты (замените на свои настройки)
  const transporter = nodemailer.createTransport({
    service: 'gmail',
    auth: {
      user: senderEmail,
      pass: senderPassword,
    },
  });

  // Опции для отправки письма
  const mailOptions = {
    from: senderEmail,
    to: senderEmail,
    subject: 'Test Email',
    text: message,
  };

  // Возвращаем Promise для асинхронности
  return new Promise((resolve, reject) => {
    // Отправляем письмо
    transporter.sendMail(mailOptions, (error, info) => {
      if (error) {
        console.log(error);
        reject(error);
      } else {
        console.log('Email sent: ' + info.response);
        resolve(info);
      }
    });
  });
}

// Экспортируем функцию
module.exports = { send };
